contracts/SqudyToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
// import "./ISqudyToken.sol"; // Not needed for implementation

/**
 * @title SqudyToken
 * @dev Real SQUDY token with deflationary burn mechanics
 * @author Squdy Team
 */
contract SqudyToken is ERC20, ERC20Burnable, Ownable, Pausable {

    // ============ CONSTANTS ============
    // Max cap for the token supply (1B)
    uint256 public constant MAX_SUPPLY = 1_000_000_000 * 10**18;
    // Initial supply minted to owner on deploy (100M)
    uint256 public constant INITIAL_SUPPLY = 100_000_000 * 10**18;

    // ============ STATE VARIABLES ============
    mapping(address => bool) public authorizedBurners; // Contracts that can burn tokens
    uint256 public totalBurned; // Track total burned for analytics

    // ============ EVENTS ============
    event BurnerAuthorized(address indexed burner, bool authorized);
    event TokensBurnedByCampaign(address indexed campaign, uint256 amount);

    // ============ MODIFIERS ============
    modifier onlyAuthorizedBurner() {
        require(authorizedBurners[msg.sender] || msg.sender == owner(), "Not authorized to burn");
        _;
    }

    // ============ CONSTRUCTOR ============
    constructor(address initialOwner) ERC20("SQUDY Token", "SQUDY") Ownable(initialOwner) {
        // Mint initial supply to owner
        _mint(initialOwner, INITIAL_SUPPLY);

        // Owner is automatically authorized to burn
        authorizedBurners[initialOwner] = true;

        emit BurnerAuthorized(initialOwner, true);
    }

    // ============ OWNER FUNCTIONS ============

    /**
     * @dev Authorize/deauthorize contracts to burn tokens (e.g., campaign managers)
     */
    function setAuthorizedBurner(address burner, bool authorized) external onlyOwner {
        authorizedBurners[burner] = authorized;
        emit BurnerAuthorized(burner, authorized);
    }

    /**
     * @dev Emergency pause functionality
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause functionality
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    // ============ BURN FUNCTIONS ============

    /**
     * @dev Burn tokens from a specific address (for campaign managers)
     * @param from Address to burn tokens from
     * @param amount Amount to burn
     */
    function burnFrom(address from, uint256 amount) public override(ERC20Burnable) onlyAuthorizedBurner {
        require(from != address(0), "Cannot burn from zero address");
        require(balanceOf(from) >= amount, "Insufficient balance to burn");

        _burn(from, amount);
        totalBurned += amount;

        emit TokensBurnedByCampaign(msg.sender, amount);
    }

    // ============ MINT / RESCUE (OWNER) ============

    /**
     * @dev Mint new tokens up to MAX_SUPPLY cap. Testnet utility and controlled issuance.
     */
    function mint(address to, uint256 amount) external onlyOwner whenNotPaused {
        require(to != address(0), "Invalid recipient");
        require(totalSupply() + amount <= MAX_SUPPLY, "Cap exceeded");
        _mint(to, amount);
    }

    /**
     * @dev Recover ERC20 tokens mistakenly sent to this contract.
     */
    function recoverERC20(address token, address to, uint256 amount) external onlyOwner {
        require(to != address(0), "Invalid recipient");
        IERC20(token).transfer(to, amount);
    }

    /**
     * @dev Recover this token mistakenly sent to its own address.
     */
    function recoverOwnTokens(address to, uint256 amount) external onlyOwner {
        require(to != address(0), "Invalid recipient");
        require(balanceOf(address(this)) >= amount, "Insufficient contract balance");
        _transfer(address(this), to, amount);
    }

    /**
     * @dev Recover native ETH accidentally sent to this contract.
     */
    function recoverETH(address payable to) external onlyOwner {
        require(to != address(0), "Invalid recipient");
        to.transfer(address(this).balance);
    }

    /**
     * @dev Enhanced burn function that tracks total burned
     */
    function burn(uint256 amount) public override(ERC20Burnable) {
        super.burn(amount);
        totalBurned += amount;
    }

    // ============ VIEW FUNCTIONS ============

    /**
     * @dev Get the circulating supply (total supply minus burned)
     */
    function circulatingSupply() external view returns (uint256) {
        return totalSupply();
    }

    /**
     * @dev Get burn statistics
     */
    function getBurnStats() external view returns (uint256 burned, uint256 circulating, uint256 burnRate) {
        burned = totalBurned;
        circulating = totalSupply();
        burnRate = INITIAL_SUPPLY > 0 ? (burned * 10000) / INITIAL_SUPPLY : 0; // Basis points
    }

    // ============ OVERRIDES ============

    /**
     * @dev Override transfer to add pause functionality
     */
    function _update(address from, address to, uint256 value) internal override whenNotPaused {
        super._update(from, to, value);
    }

    /**
     * @dev Disable renouncing ownership for security
     */
    function renounceOwnership() public view override onlyOwner {
        revert("Ownership cannot be renounced");
    }
}

contracts/SqudyTokenV2.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title SqudyTokenV2
 * @notice Mintable/Pausable/Burnable ERC20 with roles and a controlled faucet for testnets.
 * - Roles: DEFAULT_ADMIN, MINTER, BURNER, PAUSER, RESCUER, FAUCET
 * - Cap: totalSupply may not exceed `cap`
 * - Faucet: role-gated mint with per-tx cap (for Sepolia testing)
 * - Recover functions: rescue other ERC20s or this token accidentally sent to the contract
 */
contract SqudyTokenV2 is ERC20, ERC20Burnable, AccessControl, Pausable {
  bytes32 public constant MINTER_ROLE  = keccak256("MINTER_ROLE");
  bytes32 public constant BURNER_ROLE  = keccak256("BURNER_ROLE");
  bytes32 public constant PAUSER_ROLE  = keccak256("PAUSER_ROLE");
  bytes32 public constant RESCUER_ROLE = keccak256("RESCUER_ROLE");
  bytes32 public constant FAUCET_ROLE  = keccak256("FAUCET_ROLE");

  // Hard cap on total supply
  uint256 public immutable cap;

  // Faucet configuration (per-transaction limit)
  uint256 public faucetMaxPerTx;

  event FaucetMaxPerTxUpdated(uint256 newMax);

  constructor(
    string memory name_,
    string memory symbol_,
    address admin_,
    uint256 initialSupply,
    uint256 cap_
  ) ERC20(name_, symbol_) {
    require(admin_ != address(0), "admin=0");
    require(cap_ > 0, "cap=0");
    require(initialSupply <= cap_, "initial > cap");
    cap = cap_;

    _grantRole(DEFAULT_ADMIN_ROLE, admin_);
    _grantRole(MINTER_ROLE, admin_);
    _grantRole(PAUSER_ROLE, admin_);
    _grantRole(RESCUER_ROLE, admin_);
    _grantRole(FAUCET_ROLE, admin_);

    faucetMaxPerTx = 1_000 ether; // sensible default for testnets

    if (initialSupply > 0) {
      _mint(admin_, initialSupply);
    }
  }

  // ============ Minting/Burning ============

  function mint(address to, uint256 amount) external onlyRole(MINTER_ROLE) {
    require(totalSupply() + amount <= cap, "cap exceeded");
    _mint(to, amount);
  }

  // Restrict burnFrom to burner role (holders can still call burn for their own balance)
  function burnFrom(address account, uint256 amount) public override onlyRole(BURNER_ROLE) {
    _burn(account, amount);
  }

  // ============ Faucet (testnet utility) ============

  function faucet(address to, uint256 amount) external onlyRole(FAUCET_ROLE) {
    require(amount > 0 && amount <= faucetMaxPerTx, "invalid faucet amount");
    require(totalSupply() + amount <= cap, "cap exceeded");
    _mint(to, amount);
  }

  function setFaucetMaxPerTx(uint256 newMax) external onlyRole(DEFAULT_ADMIN_ROLE) {
    faucetMaxPerTx = newMax;
    emit FaucetMaxPerTxUpdated(newMax);
  }

  // ============ Pause Controls ============

  function pause() external onlyRole(PAUSER_ROLE) { _pause(); }
  function unpause() external onlyRole(PAUSER_ROLE) { _unpause(); }

  function _update(address from, address to, uint256 value)
    internal
    override(ERC20)
    whenNotPaused
  {
    super._update(from, to, value);
  }

  // ============ Rescue Utilities ============

  function recoverERC20(address token, address to, uint256 amount) external onlyRole(RESCUER_ROLE) {
    require(to != address(0), "to=0");
    IERC20(token).transfer(to, amount);
  }

  function recoverOwnTokens(address to, uint256 amount) external onlyRole(RESCUER_ROLE) {
    require(to != address(0), "to=0");
    _transfer(address(this), to, amount);
  }
}

